    // CLAIRE TRYING
    function useFetch(query, page){
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(false);
        const [list, setList] = useState([]);

        const sendQuery = useCallback(async() => {
            try{
                await setLoading(true);
                await setError(false);
                const res = await axios.get(`https://api.themoviedb.org/3/movie/${sort}?&language=en-US&page=${page}`);
                await setList((prev) => [...prev, ...res.data]);
                setLoading(false);
            } catch(err){
                setError(err);
            }
        }, [query, page]);
        useEffect(()=> {
            sendQuery(query);
        }, [query, sendQuery, page]);
        return { loading, error, list };

    }



    // Banner
    const fetchPopularMovies = async() => {
        const res = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`, {
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                Authorization: "Bearer " + API_TOKEN,
            },
        });
        let popularMovieData = await res.json();
        popularMovieData = popularMovieData.results.splice(0, 10);
        setMovieDataBanner(popularMovieData);
        setMovieBannerLength(popularMovieData.length);

    };
    useEffect( async () => {
        await fetchPopularMovies();
    }, []);

    function handleLoadMore(){
        setPage(page + 1);
    }

    return (
