import React from 'react';
import { useEffect, useState } from 'react';
import movieCard from '../components/MovieCard';
import { API_KEY, API_TOKEN } from '../globals/globals';
import Movies from '../components/Movies';

function PageSearch({ sort }) {

    const [q, setQ] = useState("");
    let [movieData, setMovieData] = useState(null);

    // search by movie title only
    const [searchParam, setSearchParam] = useState(["title"]);

    useEffect(()=> {
        // using async since we need the data before load the page
        const fetchMovies = async() => {
            // fetch() - built in function
            const res = await fetch(`https://api.themoviedb.org/3/movie/${sort}?&language=en-US&page=1`, {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer '+ API_TOKEN 
                }

            });
            let rawMovieData = await res.json();

            setMovieData.push(rawMovieData)




        }
        fetchMovies();

    }, [sort])

  return (
    <div>
        <label htmlFor="search-form">
            <input type="search" name="search-form" id="search-form" className="search-input" placeholder="Search for..." value={q} onChange = {(e)=>setQ(e.target.value)}/>
            <span>Search movies here...</span>
        </label>

        <ul>
            <li>
             {/* <Movies movieData={movieData} /> */}
             <Movies />
            </li>
        </ul>
    </div>
  )
}

export default PageSearch
