                setMovieData3(rawMovieData);
            }else if(page === 4) {
                setMovieData4(rawMovieData);
            }else if(page === 5) {
                setMovieData5(rawMovieData);
            }

        }

        fetchMovies();
    }, [sort, page]);


  return (
    <div>PageSearch</div>
  )
}

